import React,{Component} from 'react';

class ErrorBoundary extends Component{
    constructor(props){
        super(props);
        this.state = {
            hasError : false
        }
    }

    render()
    {
        if(this.state.hasError)
        {
            return <h1>Oooops. that is not good.</h1>
        }
        return this.props.children
    }
}
export default ErrorBoundary;
